sourcesContent:
import bcrypt from 'bcryptjs';
import jwt from 'jsonwebtoken';
import { User, OTP } from '../models/index.js';
import { sendOTP } from '../services/emailService.js';
import dotenv from 'dotenv';
dotenv.config();

const salt = bcrypt.genSaltSync(10);

const generateOTP = () => Math.floor(100000 + Math.random() * 900000).toString();

const register = async (req, res) => {
    const { email, password, firstName, lastName } = req.body;
    try {
        const existingUser = await User.findOne({ where: { email } });
        if (existingUser) return res.status(400).json({ message: 'Email đã tồn tại' });

        const hashPassword = bcrypt.hashSync(password, salt);
        const newUser = await User.create({
            email,
            password: hashPassword,
            firstName,
            lastName,
            isVerified: false,
        });

        const otp = generateOTP();
        await sendOTP(email, otp, 'register');
        await OTP.create({
            userId: newUser.id,
            otp: bcrypt.hashSync(otp, salt),
            expiresAt: new Date(Date.now() + 5 * 60 * 1000),
            type: 'register',
        });

        res.status(201).json({ message: 'Đăng ký thành công. Vui lòng kiểm tra email để nhận OTP.', userId: newUser.id });
    } catch (error) {
        res.status(500).json({ message: 'Lỗi khi đăng ký', error: error.message });
    }
};

const verifyOTP = async (req, res) => {
    const { userId, otp } = req.body;
    try {
        const otpRecord = await OTP.findOne({ where: { userId, type: 'register' } });
        if (!otpRecord || otpRecord.expiresAt < new Date()) return res.status(400).json({ message: 'OTP không hợp lệ hoặc đã hết hạn' });

        const isValid = bcrypt.compareSync(otp, otpRecord.otp);
        if (!isValid) return res.status(400).json({ message: 'OTP không đúng' });

        const user = await User.findByPk(userId);
        user.isVerified = true;
        await user.save();
        await otpRecord.destroy();

        res.status(200).json({ message: 'Xác thực email thành công' });
    } catch (error) {
        res.status(500).json({ message: 'Lỗi khi xác thực OTP', error: error.message });
    }
};

const login = async (req, res) => {
    const { email, password } = req.body;
    try {
        const user = await User.findOne({ where: { email } });
        if (!user || !user.isVerified) return res.status(400).json({ message: 'Email không tồn tại hoặc chưa được xác thực' });

        const isValid = bcrypt.compareSync(password, user.password);
        if (!isValid) return res.status(400).json({ message: 'Mật khẩu không đúng' });

        const token = jwt.sign({ userId: user.id, email: user.email }, process.env.JWT_SECRET, { expiresIn: '1h' });
        res.status(200).json({ token, user: { id: user.id, email: user.email, firstName: user.firstName, lastName: user.lastName } });
    } catch (error) {
        res.status(500).json({ message: 'Lỗi khi đăng nhập', error: error.message });
    }
};

const forgotPassword = async (req, res) => {
    const { email } = req.body;
    try {
        const user = await User.findOne({ where: { email } });
        if (!user) return res.status(400).json({ message: 'Email không tồn tại' });

        const otp = generateOTP();
        await sendOTP(email, otp, 'forgot');
        await OTP.create({
            userId: user.id,
            otp: bcrypt.hashSync(otp, salt),
            expiresAt: new Date(Date.now() + 5 * 60 * 1000),
            type: 'forgot',
        });

        res.status(200).json({ message: 'OTP đã được gửi đến email', userId: user.id });
    } catch (error) {
        res.status(500).json({ message: 'Lỗi khi yêu cầu quên mật khẩu', error: error.message });
    }
};

const resetPassword = async (req, res) => {
    const { userId, otp, newPassword } = req.body;
    try {
        const otpRecord = await OTP.findOne({ where: { userId, type: 'forgot' } });
        if (!otpRecord || otpRecord.expiresAt < new Date()) return res.status(400).json({ message: 'OTP không hợp lệ hoặc đã hết hạn' });

        const isValid = bcrypt.compareSync(otp, otpRecord.otp);
        if (!isValid) return res.status(400).json({ message: 'OTP không đúng' });

        const hashPassword = bcrypt.hashSync(newPassword, salt);
        const user = await User.findByPk(userId);
        user.password = hashPassword;
        await user.save();
        await otpRecord.destroy();

        res.status(200).json({ message: 'Đặt lại mật khẩu thành công' });
    } catch (error) {
        res.status(500).json({ message: 'Lỗi khi đặt lại mật khẩu', error: error.message });
    }
};

export { register, verifyOTP, login, forgotPassword, resetPassword };

