sourcesContent:
import bcrypt from 'bcryptjs';
import jwt from 'jsonwebtoken';
import { User, OTP } from '../models/index';
import { sendOTP } from '../services/emailService';
import dotenv from 'dotenv';
dotenv.config();

const salt = bcrypt.genSaltSync(10);

const generateOTP = () => Math.floor(100000 + Math.random() * 900000).toString();

const register = async (req, res) => {
    const { email, password, firstName, lastName } = req.body;
    try {
        console.log('Request body:', req.body); // Log dữ liệu nhận được
        if (!email || !password || !firstName || !lastName) {
            return res.status(400).json({ message: 'Vui lòng cung cấp đầy đủ email, password, firstName, lastName' });
        }
        const existingUser = await User.findOne({ where: { email } });
        if (existingUser) return res.status(400).json({ message: 'Email already exists' });

        const hashPassword = bcrypt.hashSync(password, salt);
        const newUser = await User.create({
            email,
            password: hashPassword,
            firstName,
            lastName,
        });
        console.log('User created:', newUser.id); // Log user ID

        const otp = generateOTP();
        console.log('Generated OTP:', otp); // Log OTP
        await sendOTP(email, otp, 'register');
        console.log('OTP sent to:', email); // Log gửi email

        await OTP.create({
            userId: newUser.id,
            otp: bcrypt.hashSync(otp, salt),
            expiresAt: new Date(Date.now() + 5 * 60 * 1000),
            type: 'register',
        });
        console.log('OTP saved to database'); // Log lưu OTP

        res.status(201).json({ message: 'User registered. Check email for OTP.', userId: newUser.id });
    } catch (error) {
        console.error('Lỗi đăng ký:', error.message, error.stack); // Log chi tiết
        res.status(500).json({ message: 'Error in registration', error: error.message });
    }
};

const verifyOTP = async (req, res) => {
    const { userId, otp } = req.body;
    try {
        const otpRecord = await OTP.findOne({ where: { userId, type: 'register' } });
        if (!otpRecord || otpRecord.expiresAt < new Date()) return res.status(400).json({ message: 'Invalid or expired OTP' });

        const isValid = bcrypt.compareSync(otp, otpRecord.otp);
        if (!isValid) return res.status(400).json({ message: 'Invalid OTP' });

        const user = await User.findByPk(userId);
        user.isVerified = true; // Add isVerified field in model if needed
        await user.save();
        await otpRecord.destroy();

        res.status(200).json({ message: 'Email verified successfully' });
    } catch (error) {
        res.status(500).json({ message: 'Error in OTP verification', error: error.message });
    }
};

const login = async (req, res) => {
    const { email, password } = req.body;
    try {
        const user = await User.findOne({ where: { email } });
        if (!user) return res.status(400).json({ message: 'Invalid email or password' });

        const isValid = bcrypt.compareSync(password, user.password);
        if (!isValid) return res.status(400).json({ message: 'Invalid email or password' });

        const token = jwt.sign({ userId: user.id, email: user.email }, process.env.JWT_SECRET, { expiresIn: '1h' });
        res.status(200).json({ token });
    } catch (error) {
        res.status(500).json({ message: 'Error in login', error: error.message });
    }
};

const forgotPassword = async (req, res) => {
    const { email } = req.body;
    try {
        const user = await User.findOne({ where: { email } });
        if (!user) return res.status(400).json({ message: 'Email not found' });

        const otp = generateOTP();
        await sendOTP(email, otp, 'forgot');
        await OTP.create({
            userId: user.id,
            otp: bcrypt.hashSync(otp, salt),
            expiresAt: new Date(Date.now() + 5 * 60 * 1000),
            type: 'forgot',
        });

        res.status(200).json({ message: 'OTP sent to email', userId: user.id });
    } catch (error) {
        res.status(500).json({ message: 'Error in forgot password', error: error.message });
    }
};

const resetPassword = async (req, res) => {
    const { userId, otp, newPassword } = req.body;
    try {
        const otpRecord = await OTP.findOne({ where: { userId, type: 'forgot' } });
        if (!otpRecord || otpRecord.expiresAt < new Date()) return res.status(400).json({ message: 'Invalid or expired OTP' });

        const isValid = bcrypt.compareSync(otp, otpRecord.otp);
        if (!isValid) return res.status(400).json({ message: 'Invalid OTP' });

        const hashPassword = bcrypt.hashSync(newPassword, salt);
        const user = await User.findByPk(userId);
        user.password = hashPassword;
        await user.save();
        await otpRecord.destroy();

        res.status(200).json({ message: 'Password reset successfully' });
    } catch (error) {
        res.status(500).json({ message: 'Error in reset password', error: error.message });
    }
};

export { register, verifyOTP, login, forgotPassword, resetPassword };

