sourcesContent:
import { DataTypes, Model } from 'sequelize';

class OTP extends Model {
    static init(sequelize) {
        super.init({
            id: {
                type: DataTypes.INTEGER,
                autoIncrement: true,
                primaryKey: true,
            },
            userId: {
                type: DataTypes.INTEGER,
                allowNull: false,
            },
            otp: {
                type: DataTypes.STRING,
                allowNull: false,
            },
            expiresAt: {
                type: DataTypes.DATE,
                allowNull: false,
            },
            type: DataTypes.STRING, // 'register' or 'forgot'
        }, {
            sequelize,
            modelName: 'OTP',
        });
    }

    static associate() {
        OTP.belongsTo(User, { foreignKey: 'userId' });
    }
}

export default OTP;

